rder.',   `billing_phone` varchar(255) NOT NULL DEFAULT '' COMMENT 'The phone number for the billing address.',   `billing_company` varchar(255) NOT NULL DEFAULT '' COMMENT 'The company of the billing address.',   `billing_street1` varchar(255) NOT NULL DEFAULT '' COMMENT 'The street address where the bill will be sent.',   `billing_street2` varchar(255) NOT NULL DEFAULT '' COMMENT 'The second line of the street address.',   `billing_city` varchar(255) NOT NULL DEFAULT '' COMMENT 'The city where the bill will be sent.',   `billing_zone` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT 'The state/zone/province ID where the bill will be sent.',   `billing_postal_code` varchar(255) NOT NULL DEFAULT '' COMMENT 'The postal code where the bill will be sent.',   `billing_country` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT 'The country ID where the bill will be sent.',   `payment_method` varchar(32) NOT NULL DEFAULT '' COMMENT 'The method of payment.',   `data` text COMMENT 'A serialized array of extra data.',   `created` int(11) NOT NULL DEFAULT '0' COMMENT 'The Unix timestamp indicating when the order was created.',   `modified` int(11) NOT NULL DEFAULT '0' COMMENT 'The Unix timestamp indicating when the order was last modified.',   `host` varchar(255) NOT NULL DEFAULT '' COMMENT 'Host IP address of the person paying for the order.',   `currency` char(3) NOT NULL DEFAULT '' COMMENT 'The ISO currency code for the order.',   PRIMARY KEY (`order_id`),   KEY `uid` (`uid`),   KEY `order_status` (`order_status`) ) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='Stores Ubercart order information.' AUTO_INCREMENT=5;
